package com.demo.store.utils;import android.util.Log;import java.io.BufferedWriter;import java.io.FileNotFoundException;import java.io.FileWriter;import java.io.IOException;/** * ============================================================ *  * 版权 ：安博教育集团 版权所有 (c) 2013 *  * 版本 ：var *  * 创建日期 ： 2013-1-17 下午12:15:25 *  * 描述 ： *  *     日志打印工具类 * 修订历史 ：  * ============================================================ **/public final class AbLog {	public static boolean DEBUG = true;		private AbLog() {	}		/**	 * 	 * @param obj	 * @param msg	 */	public static void e(Object obj, String msg) {		if (DEBUG) {			Log.e(obj.getClass().getSimpleName(), msg);		}	}		/**	 * 	 * @param obj	 * @param msg	 */	public static void i(Object obj, String msg) {		if (DEBUG) {			Log.i(obj.getClass().getSimpleName(), msg);		}	}		/**	 * 	 * 	 * @param obj	 * @param msg	 */	public static void d(Object obj, String msg) {		if (DEBUG) {			Log.d(obj.getClass().getSimpleName(), msg);		}	}		/**	 * 	 * 	 * @param obj	 * @param msg	 */	public static void w(Object obj, String msg) {		if (DEBUG) {			Log.w(obj.getClass().getSimpleName(), msg);		}	}		/**	 * 	 * 	 * @param obj	 * @param msg	 */	public static void v(Object obj, String msg) {		if (DEBUG) {			Log.v(obj.getClass().getSimpleName(), msg);		}	}		/**	 * 打印log到文件中	 * 	 * @param logPath 日志文件路径	 * @param logData 日志内容	 * @param override 是否覆盖文件内容	 */	public static void f(String logPath, String logData, boolean override) {		BufferedWriter writer = null;		try {			writer = new BufferedWriter(new FileWriter(logPath, !override));			if (!override) {				writer.append(logData);			} else {				writer.write(logData);			}			writer.newLine();		} catch (FileNotFoundException e) {			e(new AbLog(), e.toString());		} catch (IOException e) {			e(new AbLog(), e.toString());		} finally {			try {				if (writer != null) {					writer.flush();					writer.close();				}			} catch (Exception e2) {				e(new AbLog(), e2.toString());			}		}	}}